import React, { useState } from "react";
// import axios from "axios";
import { useHistory } from "react-router-dom";
import styled from "styled-components";
import logo from "../assets/logo.svg";
// import Input from "./Input";

const RegisterForm = () => {
  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    email: "",
    password: ""
  })

  const history = useHistory();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value})
  }

  const options = {
    method: 'POST',
    headers: {
      Accept: 'application/json',
      'Content-Type': 'application/json',
      Authorization: 'Bearer cwzmajfp2v0lf0lo8eb5'
    },
    body: JSON.stringify({
      first_name: formData.first_name,
      last_name: formData.last_name,
      email: formData.email,
      phone: '09009900770',
      dob: '12/12/1995',
      gender: 'female',
      address: {
        street_line_one: 'No, 20 Aso Villaa',
        street_line_two: 'Off Mambila Barracksa',
        post_code: '900991',
        city: 'Abuja',
        state: 'FCT',
        country: 'Nigeria'
      }
    })
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('https://demo-api.pneumahealth.co/patients/', options)
    .then(res => res.json())
    .then((res) => {
      console.log(res.dat)
      history.push("/profile")
    })
    .catch((error) => {
      console.log(error)
    })
    console.log(formData)
  }
  return (
    <Container>
      <LogoWrapper>
        <img src={logo} alt="" />
        <h3>
          MEDCONSULT
        </h3>
      </LogoWrapper>
      <Form onSubmit={handleSubmit}>
        <h3>Sign Up</h3>
        <FormContainer>
          <Input name="first_name" type="text" placeholder="FirstName" value={formData.first_name} onChange={handleChange} required  />
          <Status />
        </FormContainer>
        <FormContainer>
        <Input name="last_name" type="text" placeholder="LastName" value={formData.last_name} onChange={handleChange} required />
          <Status />
        </FormContainer>
        <FormContainer>
        <Input name="email" type="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
          <Status />
        </FormContainer>
        <FormContainer>
        <Input name="password" type="password" placeholder="Password" value={formData.password} onChange={handleChange} required />
          <Status />
        </FormContainer>
        <FormContainer>
        <Input type="password" placeholder="Confrim Password" required />
          <Status />
        </FormContainer>
        <button type="submit">Sign Up</button>
      </Form>
      <div>
        <Terms>
          By signing up, I agree to the Privacy Policy <br /> and Terms of
          Service
        </Terms>
        <h4>
          Already have an account? <a href="/">Sign In</a>
        </h4>
      </div>
    </Container>
  );
};

const Terms = styled.p`
  padding: 0 1rem;
  text-align: center;
  font-size: 10px;
  color: #808080;
  font-weight: 300;
`;

const Form = styled.form`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  h3 {
    color: #666666;
    margin-bottom: 2rem;
  }
  button {
    width: 75%;
    max-width: 350px;
    min-width: 250px;
    height: 40px;
    border: none;
    margin: 1rem 0;
    box-shadow: 0px 14px 9px -15px rgba(0, 0, 0, 0.25);
    border-radius: 8px;
    background-color: #70edb9;
    color: #fff;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.2s ease-in;
    &:hover {
      transform: translateY(-3px);
    }
  }
`;

const LogoWrapper = styled.div`
  img {
    height: 6rem;
  }
  h3 {
    color: #ff8d8d;
    text-align: center;
    font-size: 22px;
  }
  span {
    color: #5dc399;
    font-weight: 300;
    font-size: 18px;
  }
`;

const Container = styled.div`
  min-width: 400px;
  backdrop-filter: blur(35px);
  background-color: rgba(255, 255, 255, 0.8);
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
  align-items: center;
  padding: 0 2rem;
  @media (max-width: 900px) {
    width: 100vw;
    position: absolute;
    padding: 0;
  }
  h4 {
    color: #808080;
    font-weight: bold;
    font-size: 13px;
    margin-top: 2rem;
    a {
      color: #ff8d8d;
      cursor: pointer;
    }
  }
`;

const Input = styled.input`
  width: 80%;
  max-width: 350px;
  min-width: 250px;
  height: 40px;
  border: none;
  margin: 0.5rem 0;
  background-color: #f5f5f5;
  box-shadow: 0px 14px 9px -15px rgba(0, 0, 0, 0.25);
  border-radius: 8px;
  padding: 0 1rem;
  transition: all 0.2s ease-in;
  &:hover {
    transform: translateY(-3px);
  }
`;

const FormContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Status = styled.div`
  height: 10px;
  width: 10px;
  background: #9d9d9d;
  border-radius: 10px;
  margin-left: 1rem;
  ${Input}:focus + & {
    background: #ffa689;
  }
  ${Input}:invalid + & {
    background: #fe2f75;
  }
  ${Input}:valid + & {
    background: #70edb9;
  }
`;

export default RegisterForm;
